import { useState } from "react";
import "./App.css"

type Note = {
  id : number;
  title : string;
  content : string;
}

const App = () => {
  const [notes, setNotes] = useState<Note[]>([
    {
      id : 1,
      title : "note title 1",
      content : "content 1",
    },
    {
      id : 2,
      title : "note title 2",
      content : "content 2",
    },
    {
      id : 3,
      title : "note title 3",
      content : "content 3",
    },
    {
      id : 4,
      title : "note title 4",
      content : "content 4",
    }
  ]);
